test('remove user', async t => {
  const userTest = { email: 'remover@gmail.com' };
  const newUser = await models.user.build(userTest);
  await newUser.save();

  const removed = await models.user.destroy({ where: { email: 'remover@gmail.com' } });
  // console.log(removed)
  t.is(removed, 1);
});

